		<style type="text/css">
		/*! normalize.css v1.0.1 | MIT License | git.io/normalize */  
		article,aside,details,figcaption,figure,footer,header,hgroup,nav,section,summary { display: block;}
		audio,canvas,video { display: inline-block; *display: inline; *zoom: 1;}
		audio:not([controls]) { display: none; height: 0;}
		[hidden] { display: none;}
		html { font-size: 100%; /* 1 */ -webkit-text-size-adjust: 100%; /* 2 */ -ms-text-size-adjust: 100%; /* 2 */}
		html,button,input,select,textarea { font-family: sans-serif;}
		body { margin: 0;}
		a:focus { outline: thin dotted;}
		a:active,a:hover { outline: 0;}
		h1 {  font-size: 2em;  margin: 0.67em 0; }
		h2 {  font-size: 1.5em;  margin: 0.83em 0; }
		h3 {  font-size: 1.17em;  margin: 1em 0; }
		h4 {  font-size: 1em;  margin: 1.33em 0; }
		h5 {  font-size: 0.83em;  margin: 1.67em 0; }
		h6 {  font-size: 0.75em;  margin: 2.33em 0; }
		abbr[title] {  border-bottom: 1px dotted; }
		b, strong {  font-weight: bold; }
		blockquote {  margin: 1em 40px; }
		dfn {  font-style: italic; }
		mark {  background: #ff0;  color: #000; }
		p, pre {  margin: 1em 0; }
		code, kbd, pre, samp {  font-family: monospace, serif;  _font-family: 'courier new', monospace;  font-size: 1em; }
		pre {  white-space: pre;  white-space: pre-wrap;  word-wrap: break-word; }
		q {  quotes: none; }
		q:before, q:after {  content: '';  content: none; }
		small {  font-size: 80%; }
		sub, sup {  font-size: 75%;  line-height: 0;  position: relative;  vertical-align: baseline; }
		sup {  top: -0.5em; }
		sub {  bottom: -0.25em; }
		dl, menu, ol, ul {  margin: 1em 0; }
		dd {  margin: 0 0 0 40px; }
		menu, ol, ul {  padding: 0 0 0 40px; }
		nav ul, nav ol {  list-style: none;  list-style-image: none; }
		img {  border: 0; /* 1 */  -ms-interpolation-mode: bicubic; /* 2 */ }
		svg:not(:root) {  overflow: hidden; }
		figure {  margin: 0; }
		form {  margin: 0; }
		fieldset {  border: 1px solid #c0c0c0;  margin: 0 2px;  padding: 0.35em 0.625em 0.75em; }
		legend {  border: 0; /* 1 */  padding: 0;  white-space: normal; /* 2 */  *margin-left: -7px; /* 3 */ }
		button, input, select, textarea {  font-size: 100%; /* 1 */  margin: 0; /* 2 */  vertical-align: baseline; /* 3 */  *vertical-align: middle; /* 3 */ }
		button, input {  line-height: normal; }
		button, html input[type="button"], /* 1 */ input[type="reset"], input[type="submit"] {  -webkit-appearance: button; /* 2 */  cursor: pointer; /* 3 */  *overflow: visible;  /* 4 */ }
		button[disabled], input[disabled] {  cursor: default; }
		input[type="checkbox"], input[type="radio"] {  box-sizing: border-box; /* 1 */  padding: 0; /* 2 */  *height: 13px; /* 3 */  *width: 13px; /* 3 */ }
		input[type="search"] {  -webkit-appearance: textfield; /* 1 */  -moz-box-sizing: content-box;  -webkit-box-sizing: content-box; /* 2 */  box-sizing: content-box; }
		input[type="search"]::-webkit-search-cancel-button, input[type="search"]::-webkit-search-decoration {  -webkit-appearance: none; }
		button::-moz-focus-inner, input::-moz-focus-inner {  border: 0;  padding: 0; }
		textarea {  overflow: auto; /* 1 */  vertical-align: top; /* 2 */ }
		table {  border-collapse: collapse;  border-spacing: 0; }

		html, button, input, select, textarea {  font-family: 'Roboto', sans-serif;  font-weight:400;      color: #484848;  margin:0px;  }
		body {     font-size: 0.9em;     line-height: 1.4;  margin:0px;  }
		h1,h2,h3,h4,h5,h6{   font-family: 'Raleway', sans-serif;  font-weight:500;  }
		strong,b{   font-weight:700; }
		::-moz-selection {     background: #b3d4fc;     text-shadow: none; }
		::selection {     background: #b3d4fc;     text-shadow: none; }
		hr {     display: block;     height: 1px;     border: 0;     border-top: 1px solid #ccc;     margin: 1em 0;     padding: 0; }
		a,a:visited{   color:blue;  }
		#TopColorbar{   width:100%;   background: #6c9730;   background: linear-gradient(to right,#D22B47,#d8ad45,#A5C341);   height: 5px; }
		#TitleBar{   padding-left:0px; padding-right:0px; background-color: #42474D; height: 51px; }
		#TitleBar > div{   height:56px; position:relative; }
		#TitleBar > div > a:first-child > img{   width:140px;margin:7px 0px 0px 10px; }
		#TitleBar > div > a:last-child{   color:#FFFFFF; display:inline-block; float:right; margin:18px 10px 0px 0px; }
		#MastBar{   display:block; line-height:18px; margin-bottom:0px; color:#FFFFFF; height:56px; width:100%; }
		article{   padding:10px;   max-width:850px;  margin:0 auto;   }
		footer{   margin:10px auto 20px;   font-size:0.8em;   color:#CCCCCC;   max-width:850px;  padding:10px;  }
		footer a{   color:#999 !important;  }
		h1{   font-size:1.6em;  }
		h2{   font-size:1.4em;   margin-top:2em; }
		h4{  font-weight:700 }
		table{   max-width: 100%;  border-collapse: collapse;  border-spacing: 0;  width: 100%;  margin-bottom: 18px;  color: #686868;  border: 1px solid #cacaca; }
		table th{   min-height: 25px;  line-height: 18px;  text-align: left;  border-bottom: 1px solid #E8E8E8;  font-weight: bold;  border-right: 1px dashed #E8E8E8;  padding: 8px;  background: #8FC688;  border: none;  color: #FFF;  vertical-align: middle;  font-size:1.1em;  }
		table th.middle{   text-align: left;  border-bottom: 1px solid #E8E8E8;  font-weight: bold;  border-right: 1px dashed #E8E8E8;  padding: 8px;  background: #DEDEDE;  border: none;  color: #888;  vertical-align: middle;  font-size:1em;  }
		table td{   color: #686868;  min-height: 25px;  line-height: 18px;  text-align: left;  vertical-align: top;  border-top: 1px solid #e8e8e8;  border-right: 1px dashed #E8E8E8;  padding: 8px;  border: 1px solid #cacaca; }
		blockquote{   clear: both;  margin: 1em 0;  border: 1px solid #ccc;  padding: .5em;  background-color: #eee;  font-size:.9em; }
		blockquote h3{   font-weight:700;   margin:0px;   font-size:1.1em;  }
		blockquote p:first-of-type{   margin-top:0px;  }
		blockquote p:last-child{  margin-bottom:0px;  }
		li > ul{   margin-top:0px;  }
		div.authmethods{    }
		div.authmethods span{   display:inline-block;   margin:2px 0px 2px 5px;   color:#FFFFFF;   padding:2px 4px;   border-radius:3px;  font-size:0.8em;  }
		div.authmethods span:first-child{  margin-left:0px;  }
		div.authmethods span.oauth1{   background-color:rgb(151,204,103); }
		div.authmethods span.oauth2-teacher{   background-color:rgb(221,82,92); }
		div.authmethods span.oauth2-ta{   background-color:rgb(95,190,202); }
		div.authmethods span.oauth2-student{   background-color:rgb(86,86,86); }
		div.authmethods span.oauth2-admin{   background-color:#CCC;  }
		div.authmethods span.oauth2-all{   border:1px solid rgb(86,86,86);  color:rgb(86,86,86); }
